mapper.update:
update user
set user_name = #{userName,jdbcType=VARCHAR},
  user_password = #{userPassword,jdbcType=VARCHAR},
  role_id = #{roleId,jdbcType=VARCHAR}
where user_id = #{userId,jdbcType=VARCHAR}

mapper.select:
select user_id, user_name, user_password, role_id
from user
where user_id = #{userId,jdbcType=VARCHAR}
